examples:
  --background: var(--gray-50);
  --foreground: var(--gray-900);
  --card: 255 255 255;
  --card-foreground: var(--gray-900);
  --popover: 255 255 255;
  --popover-foreground: var(--gray-900);
  --primary: var(--primary-blue-600);
  --primary-foreground: 255 255 255;
  --secondary: var(--gray-100);
  --secondary-foreground: var(--gray-900);
  --muted: var(--gray-100);
  --muted-foreground: var(--gray-500);
  --accent: var(--accent-teal-100);
  --accent-foreground: var(--gray-900);
  --destructive: var(--error-500);
  --destructive-foreground: 255 255 255;
  --border: var(--gray-200);
  --input: var(--gray-200);
  --ring: var(--primary-blue-600);
  --radius: var(--radius-xl);

  /* Success/Growth Colors */
  --success: var(--success-500);
  --success-foreground: 255 255 255;
  --growth: var(--primary-green-500);
  --growth-foreground: 255 255 255;

  /* Warning Colors */
  --warning: var(--warning-500);
  --warning-foreground: 255 255 255;

  /* Info Colors */
  --info: var(--info-500);
  --info-foreground: 255 255 255;
}

.dark {
  /* Dark Theme Variables */
  --background: 17 24 39;

    --border: 75 85 99;
    --input: 75 85 99;
    --ring: var(--primary-blue-500);

    /* Dark theme success/growth colors */
    --success: var(--success-500);
    --success-foreground: 255 255 255;
    --growth: var(--primary-green-500);
    --growth-foreground: 255 255 255;

    /* Dark theme warning colors */
    --warning: var(--warning-500);
    --warning-foreground: 255 255 255;

    /* Dark theme info colors */
    --info: var(--info-500);
    --info-foreground: 255 255 255;
  }
}

@layer base {
  * {
    border-color: hsl(var(--border));
  }

  html {
    scroll-behavior: smooth;
  }

  body {
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
    font-family: var(--font-body);
    font-feature-settings: "rlig" 1, "calt" 1, "kern" 1;
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-rendering: optimizeLegibility;
    font-synthesis: none;
  }

  /* Typography Classes */
  .font-display {
    font-family: var(--font-display);
  }

  .font-body {
    font-family: var(--font-body);
  }

  .font-mono {
    font-family: var(--font-mono);
  }

  /* Modern Typography Scale with Responsive Sizing */
  .text-display {
    font-size: var(--font-size-4xl);
    line-height: var(--line-height-4xl);
    font-weight: 700;
    letter-spacing: -0.025em;
    font-family: var(--font-display);
  }

  @media (min-width: 640px) {
    .text-display {
      font-size: var(--font-size-5xl);
      line-height: var(--line-height-5xl);
    }
  }

  .text-h1 {
    font-size: var(--font-size-3xl);
    line-height: var(--line-height-3xl);
    font-weight: 600;
    letter-spacing: -0.025em;
    font-family: var(--font-display);
  }

  @media (min-width: 640px) {
    .text-h1 {
      font-size: var(--font-size-4xl);
      line-height: var(--line-height-4xl);
    }
  }

  .text-h2 {
    font-size: var(--font-size-2xl);
    line-height: var(--line-height-2xl);
    font-weight: 600;
    letter-spacing: -0.025em;
    font-family: var(--font-display);
  }

  @media (min-width: 640px) {
    .text-h2 {
      font-size: var(--font-size-3xl);
      line-height: var(--line-height-3xl);
    }
  }

  .text-h3 {
    font-size: var(--font-size-xl);
    line-height: var(--line-height-xl);
    font-weight: 600;
    font-family: var(--font-display);
  }

  @media (min-width: 640px) {
    .text-h3 {
      font-size: var(--font-size-2xl);
      line-height: var(--line-height-2xl);
    }
  }

  .text-h4 {
    font-size: var(--font-size-lg);
    line-height: var(--line-height-lg);
    font-weight: 600;
    font-family: var(--font-display);
  }

  @media (min-width: 640px) {
    .text-h4 {
      font-size: var(--font-size-xl);
      line-height: var(--line-height-xl);
    }
  }

  .text-body-lg {
    font-size: var(--font-size-base);
    line-height: var(--line-height-base);
    font-family: var(--font-body);
  }

  @media (min-width: 640px) {
    .text-body-lg {
      font-size: var(--font-size-lg);
